Game.PlayerJoinScript:
var instruction;
var valid = true;
// are any of the players already holding cards?
var cardsPerHand = Math.floor(Object.keys(g.cs).length / (g.pys.length+1));
for( var i in g.pys ) {
    var player = g.pys[i];
    if( player.hand.cs.length ) {
        valid = false;
    }
}
if( !valid ) {
    instruction = "Hold";
} else {
    instruction = "Draw";
}
r = {
    pa: {
        i: instruction
    },
    pp: {
        v: valid
    },
    gp: {
        c: cardsPerHand
    }
};

Game.LibraryScript:

// TODO filter out invalid players
var cardCount = g.ps.c;
var failed = g.drawDeck.cardIds.indexOf(c.id) < 0 || !p.ps.v;
var instruction;
var first = false;
if( p.hand.cs.length >= cardCount - 1 ) {
    // are we the last to draw our last card?
    var last = true;
    for( var i in g.pys ) {
        var player = g.pys[i];
        if( player.hand.cs.length < cardCount && player.id != p.id ) {
            last = false;
        }
    }
    if( last ) {
        // the last person to draw plays
        instruction = "Play";
        first = true;
    } else {
        instruction = "Hold";
    }
} else {
    instruction = "Draw";
}
// next action
var actions = {};
actions[p.id] = {
    i: instruction
};
var playerProperties = {};
playerProperties[p.id] = {
    f: first
};
r = {
    as: actions,
    f: failed,
    pps: playerProperties
};


// TODO filter out invalid players

var first = p.ps.f;
var instruction;
var actions = {};
if(first) {
    for( var i in g.pys ) {
        var py = g.pys[i];
        actions[py.id] = {
            i: "Play"
        }
    }
}
actions[p.id] = {
    i: "Hold"
};
var last = true;
var winner;
var winValue = 0;
for( var i in g.pys ) {
    var py = g.pys[i];
    var d = g.ps[py.id];
    if( py.id == p.id ) {
        d = c.id;
    }
    if( !d ) {
        last = false;
    } else {
        if( d > winValue ) {
            winner = py;
            winValue = d;
        }
    }
}
var playerProperties = {};
var gameProperties = {};
if( last ) {
    for( var i in g.pys ) {
        var py = g.pys[i];
        actions[py.id] = {
            i: "Hold"
        };
        gameProperties[py.id] = null;
        playerProperties[py.id] = {f: py.id==winner.id};
    }
    var instruction;
    if( p.hand.cs.length > 1 ) {
        // if its the last hand, nobody can play
        instruction = "Play";
    } else {
        instruction = "NoGame";
    }
    actions[winner.id] = {
        i: instruction
    };
} else {
    gameProperties[p.id] = c.id;
}

var failed = !p.ps.v;

/*
out.println("actions "+JSON.stringify(actions));
out.println("gameProperties "+JSON.stringify(gameProperties));
out.println("playerProperties "+JSON.stringify(playerProperties));
out.println("f "+JSON.stringify(failed));
*/

r = {
    as: actions,
    gps: gameProperties,
    pps: playerProperties,
    f: failed
};
